import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login, register } from './user-api';
import './App.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await login(username, password);
      setMessage(response);
      if (response === 'Login successful!') {
        navigate('/'); // Redirect to homepage after successful login
      }
    } catch (error) {
      setMessage(error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await register(username, password);
      setMessage(response);
      if (response === 'Registration successful!') {
        navigate('/'); // Redirect to homepage after successful registration
      }
    } catch (error) {
      setMessage(error);
    }
  };

  const toggleForm = () => {
    setIsLogin(!isLogin);
    setMessage('');
    setUsername('');
    setPassword('');
  };

  return (
    <div className="App">
      <div className="window">
        <div className="title-bar">
          <span>{isLogin ? 'Login' : 'Register'}</span>
          <div className="window-buttons">
            <div className="window-button minimize"></div>
            <div className="window-button maximize"></div>
            <div className="window-button close"></div>
          </div>
        </div>
        <div className="form-container">
          <form onSubmit={isLogin ? handleLogin : handleRegister}>
            <div>
              <label>Username: </label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Password: </label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
          </form>
          <button onClick={toggleForm} className="toggle-button">
            {isLogin ? 'Need to register? Sign up here' : 'Already have an account? Log in here'}
          </button>
          {message && <p>{message}</p>}
        </div>
      </div>
    </div>
  );
}

export default Login;
